var userId=data.user_id;
if (userId) {
// 获取应用实例
const app = ibiz.hub.getApp(context.srfappid);
// 获取指定实体服务
const deService = await app.deService.getService(
        context, "ibizodooapp.res_users" // 实体标识
);
//将user的值赋值到上下文
const tempContext = Object.assign({},context,{ "res_users" : userId });
const result = await deService.get(tempContext);

data.work_contact_id=result.data.partner_id;
    if  (!data.name) {
        data.name=result.data.name;
    }
}
