// 获取视图上下文
const topContext = view.context;
const appEntityId = view.model.appDataEntityId;
const entityName = appEntityId.split('.').pop(); // 取点号后最后一段

const mailThreadValue = topContext[entityName];

// 设置当前视图的上下文变量 mail_thread
// 注意：实际应用中上下文对象通常是只读的，建议通过视图参数传递
view.params.mail_thread = mailThreadValue;
view.params.srfmodelname = entityName;

const uiDomain = ibiz.uiDomainManager.get(context.srfsessionid);
if (!uiDomain) {
    return;
}

const value = data[0].message_inputbox;
if (value) {
    uiDomain.dataChange();
} else {
    uiDomain.dataChangeCompleted();
}

