// 获取目标面板项
const panelItem = view.layoutPanel.findPanelItemByName("attachment_panel");
if (panelItem) {
  // 切换可见状态
  panelItem.state.visible = !panelItem.state.visible;
}

// 获取目标面板项消息发送
const panelItem = view.layoutPanel.findPanelItemByName("message_panel");
if (panelItem) {
  // 切换可见状态
  panelItem.state.visible = false;
}

// 获取目标面板项文件列表
const panelItem2 = view.layoutPanel.findPanelItemByName("attachment_panel");
if (panelItem2) {
  // 切换可见状态
  panelItem2.state.visible = false;
}

// 获取视图上下文
const topContext = view.parentView.context;
const appEntityId = view.parentView.model.appDataEntityId;
const entityName = appEntityId.split('.').pop(); // 取点号后最后一段

const resId = topContext[entityName];

// 设置当前视图的上下文变量 mail_thread
// 注意：实际应用中上下文对象通常是只读的，建议通过视图参数传递
view.params.mail_thread = resId;
view.params.srfmodelname = entityName;
view.params.mail_activity_mixin = resId;

console.log("更新附件计数");
const attachment_count =view.layoutPanel.findPanelItemByName("container_thread").panelItems.attachment_count;
if (attachment_count) {
    attachment_count.setDataValue(ctrl.state.total);
}

console.log("更新活动状态");
const activity_state =view.layoutPanel.findPanelItemByName("activity").panelItems.activity_state;
if (activity_state && ctrl.state.total === 0) {
    activity_state.setDataValue(null);
}

// 获取目标面板项
const panelItem = view.layoutPanel.findPanelItemByName("message_panel");
if (panelItem) {
  // 切换可见状态
  panelItem.state.visible = !panelItem.state.visible;
}

const uiDomain = ibiz.uiDomainManager.get(context.srfsessionid);
if (!uiDomain) {
    return;
}

const value = data[0].message_inputbox;
if (value) {
    uiDomain.dataChange();
} else {
    uiDomain.dataChangeCompleted();
}

