getPSAppDEUIAction.scriptCode:
const file = data[0];
const otherParams = {};
// 如果有folder则以业务数据中folder作为目录 
if (file && file.folder) otherParams.osscat = file.folder;
// 计算下载地址
const { downloadUrl } = ibiz.util.file.calcFileUpDownUrl(context, params, file, otherParams);
const url = downloadUrl.replace('%fileId%', file.file_id);
// 构建下载凭证参数（实体标识和主键标识）
const downloadTicketParams = {
	appEntityTag: params.srfmodelname ? `ibizodooapp.${params.srfmodelname}` : null,
}
// 下载文件
ibiz.util.file.fileDownload(url, file.name, {
  context,
  params,
  data: file,
  file: { fileId: file.file_id, ...file },
  extraParams: {},
  downloadTicketParams,
});

