         ctrl?.disableCounter()
      }
   }
})

getPSDEFormItemUpdates.scriptCode:
if(data.flexible_hours == 1){
    return;
}
// 获取应用实例
const app = ibiz.hub.getApp(context.srfappid);
// 获取指定实体服务
const deService = await app.deService.getService(
    context,
    "ibizodooapp.resource_calendar_attendance", // 实体标识
);
// 获取实体所有缓存数据
var list = deService.local.getList();
//过滤满足业务的数据
var list1=list.filter(attendance => 
             attendance.day_period !== 'lunch' &&
            !attendance.date_from && !attendance.date_to &&
            !attendance.resource_id && !attendance.display_type);

if(list1.length == 0){
    data.hours_per_day=0;
}else{
    let hourCount = 0;
    //总小时数
    for (const attendance of list1) {
        hourCount += attendance.hour_to - attendance.hour_from;
    }
   //总天数
    let numberOfDays;
    if (data.two_weeks_calendar) {
        // 初始化一个对象来存储每周类型对应的天数
        const weekTypeGroups = {};
        // 遍历所有日历项
        for (const cal of list1) {
            // 如果该周类型不存在则初始化空集合
            if (!weekTypeGroups[cal.week_type]) {
                weekTypeGroups[cal.week_type] = new Set();
            }
            // 添加当前日期到对应周类型的集合
            weekTypeGroups[cal.week_type].add(cal.dayofweek);
        }
        
        // 计算所有周类型的总天数
        let totalDays = 0;
        for (const type in weekTypeGroups) {
            totalDays += weekTypeGroups[type].size;
        }
        numberOfDays = totalDays;
    } else {
            numberOfDays = new Set(list1.map(cal => cal.dayofweek)).size;
    }   
    if (numberOfDays === 0) {
        data.hours_per_day=0;
    }
    const average = hourCount / numberOfDays;
    data.hours_per_day=average;
}
